import React, {Component} from 'react'
import '../css/style.css'

export default class GetSheduleNew extends Component {
  constructor(props) {
      super(props);
      this.state = {
          items: []
      }
  }
  
  updateTask() {
    fetch('https://mypew.ru/php/table.php', {
        method: 'POST',
        header: {
            'Accept': 'application/json',
            'Content-Type': 'application/json',
        },
        body: JSON.stringify({
            table : 3,
            date : this.props.date,
            day_count: 1,
        })
    })
    .then (res => res.json())
    .then ((response) => {
        this.setState({
            items: response
        });
    })
  }
  componentDidUpdate(prevProps) {
    if (this.props.date !== prevProps.date) {
        this.updateTask();
        console.log(1);
    }
  }
  render() {
      const {items} = this.state.items;
      const fObj = JSON.parse(JSON.stringify(items));
      console.log(fObj);
      return (
          <ul className='container_for_tasks_2'>
              {items.map(item => (
                  <li key={item.id} className={"container_part_task " + (item === 'Мумрики' && (' task_green '))}>
                      <span className='part_task ' >{item.title}{console.log(item.title)}</span>
                  </li>
              ))}
          </ul>
      )
  }
}
