import dayjs from "dayjs";
import React, { useContext } from "react";
import GlobalContext from "../context/GlobalContext";

export default function CalendarHeader() {
  const { monthIndex, setMonthIndex } = useContext(GlobalContext);
  function handlePrevMonth() {
    setMonthIndex(monthIndex - 1);
    console.log(monthIndex);
  }
  function handleNextMonth() {
    setMonthIndex(monthIndex + 1);
    console.log(monthIndex);
  }
  return (
    <header className="px-4 py-2 flex items-center page__header">
        <div class="title__calendar">Календарь</div>
        <div class="slider">
            <div class="slider__form">
                <button onClick={handlePrevMonth} class="slider__but">
                    <img class="slider__but__image" src="img/timetable_header/previous.svg" alt="Logo" />
                </button>
                <button onClick={handleNextMonth} class="slider__but">
                    <img class="slider__but__image" src="img/timetable_header/next.svg" alt="Logo" />
                </button>
                <h2 className="ml-4 text-xl text-gray-500 font-bold">
                    {dayjs(new Date(dayjs().year(), monthIndex)).format("MMMM YYYY")}
                </h2>
            </div>
        </div>
    </header>
  );
}
