import React from 'react'

import classnames from 'classnames';

import * as calendar from './calendar'

import './index.css';

export default class Calendar extends React.Component {
    static defaultProps = {
        date: new Date(),
        years: [2021, 2022],
        monthNames: ['Январь', 'Февраль', 'Март', 'Апрель', 'Май', 'Июнь', 'Июль', 'Август', 'Сентябрь', 'Октябрь', 'Ноябрь', 'Декабрь'],
        weekDayNames: ['ПН', 'ВТ', 'СР', 'ЧТ', 'ПТ', 'СБ', 'ВС'],
        onChange: Function.prototype
    };

    state = {
        date: this.props.date,
        currentDate: new Date(),
        selectedDate: null
    };

    get year() {
        return this.state.date.getFullYear();
    }

    get month() {
        return this.state.date.getMonth();
    }

    get day() {
        return this.state.date.getDate();
    }

    handlePrevButtonClick = () => {
        const date = new Date(this.year, this.month - 1);

        this.setState({ date });
    };

    handleNextButtonClick = () => {
        const date = new Date(this.year, this.month + 1);

        this.setState({ date });
    };

    handleSelectChange = () => {
        const year = this.yearSelect.value;
        const month = this.monthSelect.value;

        const date = new Date(year, month);

        this.setState({ date });
    };

    handleDayClick = date => {
        this.setState({ selectedDate: date });

        this.props.onChange(date);
    };

    handleMonthOut = () => {
        let month = this.state.date.getMonth();

        if (month === 0) {
            month = 0 + 1;
        }
        if (month === 1) {
            month = 0 + 2;
        }
        if (month === 2) {
            month = 0 + 3;
        }
        if (month === 3) {
            month = '0' + 4;
        }
        if (month === 4) {
            month = '0' + 5;
        }
        if (month === 5) {
            month = 0 + 6;
        }
        if (month === 6) {
            month = 0 + 7;
        }
        if (month === 7) {
            month = 0 + 8;
        }
        if (month === 8) {
            month = 0 + 9;
        }
        if (month === 9) {
            month = 10;
        }
        if (month === 10) {
            month = 11;
        }
        if (month === 11) {
            month = 12;
        }

        return month;
    };


    render() {

        const { years, monthNames, weekDayNames } = this.props;
        const { currentDate, selectedDate } = this.state;

        const monthData = calendar.getMonthData(this.year, this.month);

        return (
            <div className="calendar__mini1">
                <header>
                    <button onClick={this.handlePrevButtonClick}>{'<'}</button>
                    <select
                        ref={element => this.monthSelect = element}
                        value={this.month}
                        onChange={this.handleSelectChange}
                    >
                        {monthNames.map((name, index) =>
                            <option key={name} value={index}>{name}</option>
                        )}
                    </select>
                    <select
                        ref={element => this.yearSelect = element}
                        value={this.year}
                        onChange={this.handleSelectChange}
                    >
                        {years.map(year =>
                            <option key={year} value={year}>{year}</option>
                        )}
                    </select>
                    <button onClick={this.handleNextButtonClick}>{'>'}</button>
                </header>
                <table>
                    <thead>
                        <tr>
                            {weekDayNames.map(name =>
                                <th key={name}>{name}</th>
                            )}
                        </tr>
                    </thead>

                    <tbody>
                        {monthData.map((week, index) =>
                            <tr className="week" key={index}>
                                {week.map((date, index) => date ?
                                    <td
                                        key={index}
                                        className={classnames('day', {
                                            'today': calendar.areEqual(date, currentDate),
                                            'selected': calendar.areEqual(date, selectedDate)
                                        })}
                                        onClick={() => this.handleDayClick(date)}
                                    >{date.getDate()}</td>
                                    :
                                    <td key={index} />
                                )}
                            </tr>
                        )}
                    </tbody>
                </table>
                <div className="border__bottom"></div>
                <div className="events">
                    <section className="events__info">
                        <div className="info__day">
                            <span>Сегодня</span>
                        </div>
                        <div className="info__date">
                            <span>{this.state.date.getDate()}.</span>
                            <span>{this.handleMonthOut()}.</span>
                            <span>{this.state.date.getFullYear()}</span>
                        </div>
                    </section>
                    <section className="events__items">
                        <div className="events__item">
                            <span>123123</span>
                        </div>
                        <div className="events__item">
                            <span>123123</span>
                        </div>
                        <div className="events__item">
                            <span>123123</span>
                        </div>
                        <div className="events__item">
                            <span>123123</span>
                        </div>
                        <div className="events__item">
                            <span>123123</span>
                        </div>
                    </section>
                </div>
            </div>
        );
    }
}
